Onload.find:
let currentAlg;

function init\(\) \{
(?:    |	)const animManag = initCanvas\(\);
(?:    |	)currentAlg = new (\w+)\(animManag, canvas\.width, canvas\.height\);
\}

Onload.replace:
function init() {
    // eslint-disable-next-line no-undef
    const animManag = initCanvas();
    // eslint-disable-next-line no-undef, no-unused-vars
    const currentAlg = new $1(animManag, canvas.width, canvas.height);
}

window.onload = init;
